import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { axiosInstance } from './network/index';

const MoviesAPI = () => {
  const [moviesList, setmoviesList] = useState([]);
  useEffect(() => {
    axiosInstance
      .get("popular?api_key=4c03591ec60bf980ec7a1be78c875fa5")
      .then((res) => setmoviesList(res.data.results))
      .catch((err) => console.log(err));
  }, []);

  return (
    <>
      <ul>
        {moviesList.map((movie) => {
          const image = movie.poster_path
          return (
            <div className="container">
              <div className="row my-5 justify-content-center  ">

                <div className="col-lg-3 col-md-6 col-sm-12 text-center mx-5 my-5 ">
                  <div className="card border-0 homecard" style="width: 25rem; ">
                    <div className="card-body text-center  " >
                      <Link to={`/${movie.id}`} key={movie.id} >

                        <img src={'https://image.tmdb.org/t/p/w500/' + image} ></img>
                        <h3>{movie.original_title}</h3>

                      </Link>
                    </div>
                  </div>
                </div>

              </div>
            </div>
          );
        })}
        {/* <img src={`https://image.tmdb.org/t/p/w500/${}`}/> */}
      </ul>
    </>
  );
};

export default MoviesAPI;
