const INITIAL_STATE = {
    movieID: ""
  };

  export function AddFavouriteReducer(state = INITIAL_STATE, action) {
    switch (action.type) {
      case "SET_FAVOURITE":
        // state.lang = action.payload xxxxx
        return {
          ...state,
          IsFavourite: action.payload,
        };
      default:
        return state;
    }
  }
