};

  const AddFavouriteReducer = (state = Favorite, action)=> {
    const favorite = action.payload 
    switch (action.type) {
      case "SET_FAVOURITE":
       const exist = state.find((x)=>x===favorite);
        if(exist){
          //return everything not equal to favourite
          return state.filter((x)=> x !==exist);

        }
        else{
          return {

            ...state,
            movieIDs: [...state.movieIDs, favorite],

        }
        }
      default:
        return state;
    }
  }
