import './MoviesApi.css';
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { axiosInstance } from './network/index';
import { useSelector, useDispatch } from 'react-redux';
import StarBorderIcon from '@mui/icons-material/StarBorder';
import StarIcon from '@mui/icons-material/Star';
import { amber } from '@mui/material/colors';
import { setFavourite } from './store/Ation/Action';



export default function MoviesAPI() {

  const favourite = useSelector((state) => state.favourite.movieID);


  const dispatch = useDispatch();

  const [searchData, setsearchData] = useState({
    searchVal: ""
  })
  const [moviesList, setmoviesList] = useState([]);

  useEffect(() => {
    axiosInstance
      .get("movie/popular?api_key=4c03591ec60bf980ec7a1be78c875fa5")
      .then((res) => setmoviesList(res.data.results))
      .catch((err) => console.log(err));
  }, []);

  //   const [displayFav, setdisplayFav] = useState({
  //     show: { display: 'inline' },
  //     hide: { display: 'none' }
  // });
  // function StarFilled(id) {

  //   if (favourite.indexOf(id) !== -1) {
  //     return <StarIcon sx={{ fontSize: 30, color: amber[500] }} />;
  //   }
  //   return <StarBorderIcon sx={{ fontSize: 30, color: amber[500] }}/>;
  // }
  // };
  const [displayFav, setdisplayFav] = useState({
    show: { display: 'inline' },
    hide: { display: 'none' }
  });

  const AddFav = (id, e) => {

    dispatch(setFavourite(id));
    e.target.style.display ="none"

  };

  const RemoveFav = () => {
    setdisplayFav({
      ...displayFav,
      show: { display: 'inline' },
      hide: { display: 'none' }
    })
  }
function Starred(props) {
  const isFav = props.isFav;
  if (isFav) {
    return <StarIcon sx={{ fontSize: 30, color: amber[500] }} />;
  }
  return <StarBorderIcon sx={{ fontSize: 30, color: amber[500] }} />;
}

ReactDOM.render(
  // Try changing to isLoggedIn={true}:
  <Greeting isLoggedIn={false} />,
  document.getElementById('root')
);


    return (
      <>
        <div className="container justify-content-center ">
          {/* search */}
          <div className="input-group my-3">
            <input
              type="search"
              className="form-control rounded "
              placeholder="Search"
              value={searchData.searchVal}
              onChange={(e) => {
                setsearchData({
                  ...searchData,
                  searchVal: e.target.value
                });
              }}

              aria-describedby="search-addon"
            ></input>

            <Link to={`/search/${searchData.searchVal}`} className="btn btn-outline-light" id="search-addon"   >
              search
            </Link>
          </div>

          <div className="row my-5 justify-content-center  ">
            {moviesList.map((movie) => {
              const image = movie.poster_path


              return (


                <div className="col-lg-3 col-md-6 col-sm-6 text-center my-5 " key={movie.id}>
                  <div className="card border-0 movie" >
                    <div className="card-body text-center justify-content-center " >
                      <div className='text-end' style={{ width: "15rem" }}>
                        <button className='favButton'  style={displayFav.hide} onClick={(e) => { AddFav(movie.id, e) }} >
                        <Starred />
                        </button>


                      </div>
                      <Link to={`/${movie.id}`} className="film" >
                        <img src={'https://image.tmdb.org/t/p/w500/' + image} style={{ width: "15rem" }} ></img>
                        <h3 className='pt-3'>{movie.original_title}</h3>
                      </Link>
                    </div>
                  </div>
                </div>


              );
            })}
            {/* <Stack spacing={2}>
          <Pagination count={10} variant="outlined" renderItem={(item) => (
