import axios from "axios";
import { axiosInstance } from './network/index';
import Pagination from '@mui/material/Pagination';
import PaginationItem from '@mui/material/PaginationItem';
import Stack from '@mui/material/Stack';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';


const MoviesAPI = () => {
  const [searchData, setsearchData] = useState({
    searchVal: ""
  })
  const [moviesList, setmoviesList] = useState([]);
  useEffect(() => {
    axiosInstance
      .get("popular?api_key=4c03591ec60bf980ec7a1be78c875fa5")
      .then((res) => setmoviesList(res.data.results))
      .catch((err) => console.log(err));
  }, []);

  return (
    <>
      {/* https://api.themoviedb.org/3/search/movie?api_key=4c03591ec60bf980ec7a1be78c875fa5&query={Spider-Man} */}
      <div className="container-fluid justify-content-center ">
        <div className="input-group">
