import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import { axiosInstance } from './network/index';

export default function MovieDetails() {
    const params = useParams();
    const [MovieDetails, setMovieDetails] = useState({});

    console.log(params);
    console.log(MovieDetails)

    useEffect(() => {
        axiosInstance
            .get(`${params.id}?api_key=4c03591ec60bf980ec7a1be78c875fa5/`)
            .then((res) => setMovieDetails(res))
            .catch((err) => console.log(err));
    }, []);
    const image = MovieDetails.poster_path
    return <div>
        <div className="container-fluid">
            <div className="row my-5 justify-content-center  ">
                <h1>{MovieDetails.original_title}</h1>
                <img src={'https://image.tmdb.org/t/p/w500/' + image} style={{width:" 15rem"}} ></img>
                <h3>{MovieDetails.overview}</h3>
            </div></div>
    </div>;
}
