const INITIAL_STATE = {
    movieID: ["true"]
  };

  export function AddFavouriteReducer(state ={ movieID: []}, action) {
    switch (action.type) {
      case "SET_FAVOURITE":

        return {
          ...state,
          movieID: [...state.movieID, action.payload],
        };
      default:
        return state;
    }
  }
