import './MoviesApi.css';
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { axiosInstance } from './network/index';
import { useSelector } from 'react-redux';
import StarBorderIcon from '@mui/icons-material/StarBorder';
import { amber } from '@mui/material/colors';



const MoviesAPI = () => {

  const [searchData, setsearchData] = useState({
    searchVal: ""
  })
  const [moviesList, setmoviesList] = useState([]);

  useEffect(() => {
    axiosInstance
      .get("movie/popular?api_key=4c03591ec60bf980ec7a1be78c875fa5")
      .then((res) =>  setmoviesList(res.data.results))
      .catch((err) => console.log(err));
  }, []);
  const toggleFavourite = () => {
    console.log("id")
  }

  return (
    <>
      <div className="container justify-content-center ">
        <div className="input-group my-3">
          <input
            type="search"
            className="form-control rounded "
            placeholder="Search"
            value={searchData.searchVal}
            onChange={(e) => {
              setsearchData({
                ...searchData,
                searchVal: e.target.value
              });
            }}

            aria-describedby="search-addon"
          ></input>

          <Link to={`/search/${searchData.searchVal}`} className="btn btn-outline-light" id="search-addon"   >
            search
          </Link>
        </div>

        <div className="row my-5 justify-content-center  ">
          {moviesList.map((movie) => {
            const image = movie.poster_path
            return (


              <div className="col-lg-3 col-md-6 col-sm-6 text-center my-5 " key={movie.id}>
                <div className="card border-0 movie" >
                  <div className="card-body text-center justify-content-center " >
                    <div className='text-end' style={{ width: "15rem" }}>
                      <button id='favButton' onClick={(e)=>{toggleFavourite(e.movie.id)}} >
                        <StarBorderIcon sx={{ fontSize: 30, color: amber[400] }}></StarBorderIcon>
                      </button>
                    </div>
                    <Link to={`/${movie.id}`} className="film" >
                      <img src={'https://image.tmdb.org/t/p/w500/' + image} style={{ width: "15rem" }} ></img>
                      <h3 className='pt-3'>{movie.original_title}</h3>
                    </Link>
                  </div>
                </div>
              </div>


            );
          })}
          {/* <Stack spacing={2}>
          <Pagination count={10} variant="outlined" renderItem={(item) => (
            <PaginationItem
              components={{ previous: ArrowBackIcon, next: ArrowForwardIcon }}
              {...item}
            />
          )} />
        </Stack> */}
        </div>
      </div>
    </>
  );
};

export default MoviesAPI;
