import './MovieA.css';
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { axiosInstance } from './network/index';

const MoviesAPI = () => {
  const [moviesList, setmoviesList] = useState([]);
  useEffect(() => {
    axiosInstance
      .get("popular?api_key=4c03591ec60bf980ec7a1be78c875fa5")
      .then((res) => setmoviesList(res.data.results))
      .catch((err) => console.log(err));
  }, []);

  return (
    <>
      <div className="container-fluid">
              <div className="row my-5 justify-content-center  ">
        {moviesList.map((movie) => {
          const image = movie.poster_path
          return (


                <div className="col-lg-3 col-md-4 col-sm-6 text-center mx-5 my-5 "key={movie.id}>
                  <div className="card border-0 " style={{width:" 25rem"}}>
                    <div className="card-body text-center  " >
                      <Link to={`/${movie.id}`} className="film" >

                        <img src={'https://image.tmdb.org/t/p/w500/' + image} style={{width:" 15rem"}} ></img>
                        <h3>{movie.original_title}</h3>
                      </Link>
                    </div>
                  </div>
                </div>


          );
        })}
       </div>
            </div>
    </>
  );
};

export default MoviesAPI;
