const INITIAL_STATE = {
  movieIDs: []
}
export default function AddFavouriteReducer(state = INITIAL_STATE, action){

  switch (action.type) {
    case "SET_FAVOURITE":
      const movieID=state.movieIDs
      const favorite = action.payload
      const exist = movieID.find(x => x === favorite);
      if (exist===undefined) {

        return {
          ...state,
          movieIDs: [...state.movieIDs, favorite]
        }
      }
      else{

        //return everything not equal to favourite
        const favorite = action.payload
        return state.movieIDs.filter(x => x !== favorite);

      }
    default:
      return state;
  }
}
