  const movieIDs = [];

export default function AddFavouriteReducer(state = movieIDs, action){
  const favorite = action.payload
  switch (action.type) {
    case "SET_FAVOURITE":

      const exist = state.find(x => x === favorite);
      if (exist===undefined) {

        return [
          ...state,
          favorite
        ]
      }
      else{

        //return everything not equal to favourite
        const favorite = action.payload
        return statefilter(x => x !== favorite);

      }
    default:
      return state;
  }
}
