import './MoviesApi.css';
import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { axiosInstance } from './network/index';
import Pagination from '@mui/material/Pagination';
import PaginationItem from '@mui/material/PaginationItem';
import Stack from '@mui/material/Stack';
import ArrowBackIcon from '@mui/icons-material/ArrowBack';
import ArrowForwardIcon from '@mui/icons-material/ArrowForward';

const MoviesAPI = () => {
  const [moviesList, setmoviesList] = useState([]);
  useEffect(() => {
    axiosInstance
      .get("popular?api_key=4c03591ec60bf980ec7a1be78c875fa5")
      .then((res) => setmoviesList(res.data.results))
      .catch((err) => console.log(err));
  }, []);

  return (
    <>
      {/* https://api.themoviedb.org/3/search/movie?api_key=4c03591ec60bf980ec7a1be78c875fa5&query={Spider-Man} */}
      <div className="container-fluid">
        <div class="input-group">
          <input type="search" class="form-control rounded" placeholder="Search" aria-label="Search"
            aria-describedby="search-addon" />
          <button type="button" class="btn btn-outline-primary">search</button>
        </div>
        <div className="row my-5 justify-content-center  ">
          {moviesList.map((movie) => {
            const image = movie.poster_path
            return (


              <div className="col-lg-3 col-md-4 col-sm-6 text-center mx-3 my-5 " key={movie.id}>
                <div className="card border-0 movie" style={{ width: " 25rem" }}>
                  <div className="card-body text-center  " >
                    <Link to={`/${movie.id}`} className="film" >

                      <img src={'https://image.tmdb.org/t/p/w500/' + image} style={{ width: " 15rem" }} ></img>
                      <h3 className='pt-5'>{movie.original_title}</h3>
                    </Link>
                  </div>
                </div>
              </div>


            );
          })}
          <Stack spacing={2}>
            <Pagination count={10} variant="outlined" renderItem={(item) => (
              <PaginationItem
                components={{ previous: ArrowBackIcon, next: ArrowForwardIcon }}
                {...item}
              />
            )} />
          </Stack>
        </div>
      </div>
    </>
  );
};

export default MoviesAPI;
